$.fn.userAutocomplete=function(){"use strict";this.select2({minimumInputLength:1,multiple:!0,initSelection:function(e,t){$.get(Spree.routes.user_search,{ids:e.val()},function(e){t(e)})},ajax:{url:Spree.routes.user_search,datatype:"json",data:function(e){return{q:e,token:Spree.api_key}},results:function(e){return{results:e}}},formatResult:function(e){return e.email},formatSelection:function(e){return e.email}})},$(document).ready(function(){$(".user_picker").userAutocomplete()});